import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import Modal from 'react-bootstrap/Modal';
import 'bootstrap/dist/css/bootstrap.css';
import './styles/styles.css';
import num1 from './audio/1.mp3'
import num2 from './audio/2.mp3'
import num3 from './audio/3.mp3'
import num4 from './audio/4.mp3'
import num5 from './audio/5.mp3'
import num6 from './audio/6.mp3'
import num7 from './audio/7.mp3'
import num8 from './audio/8.mp3'
import num9 from './audio/9.mp3'
import num10 from './audio/10.mp3'

import { useState, useEffect } from 'react';

function App() {
  const [show, setShow] = useState(false);
  const [doubleDigits, setDoubleDigits] = useState(true);
  const [trackedDigits, setTrackedDigits] = useState([Math.floor(Math.random() * 10) + 1, Math.floor(Math.random() * 10) + 1]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [randomNumber, setRandomNumber] = useState(0);
  const [intervalLimits, setIntervalLimits] = useState([500, 1500]);
  const [trialCount, setTrialCount] = useState(() => {
    return JSON.parse(window.localStorage.getItem('COUNT')) || 1
  });
  const [listTrials, setListTrials] = useState(() => {
    return JSON.parse(window.localStorage.getItem('LIST_TRIALS')) || []
  });
  const listAudioSource = [num1, num2, num3, num4, num5, num6, num7, num8, num9, num10];
  const listAudio = [];
  for (let i=0; i<listAudioSource.length; i++) {
    let audioFile = new Audio(listAudioSource[i]);
    listAudio.push(audioFile)
  }

  useEffect(() => {
    window.localStorage.setItem('COUNT', JSON.stringify(trialCount));
  }, [trialCount]);

  useEffect(() => {
    window.localStorage.setItem('LIST_TRIALS', JSON.stringify(listTrials));
  }, [listTrials]);

  useEffect(() => {
    const intervalId = setInterval(
      () => {
        setRandomNumber(Math.floor(Math.random() * listAudio.length));
        if (isPlaying) {
          const randomAudio = listAudio[randomNumber];
          randomAudio.play();
          console.log("play" + randomNumber);

          // update trial list
          let ts = Date.now() // seconds
          let unix = new Date(ts);
          let dictTrial = {id: listTrials.length, trial: trialCount, number: randomNumber+1, time: String(unix), valid: true};
          setListTrials([dictTrial, ...listTrials])
        }
      },
      Math.random() * (intervalLimits[1] - intervalLimits[0]) + intervalLimits[0]
      ); // Random interval between 500ms and 1500ms

    return () => clearInterval(intervalId); 
  }, [isPlaying, listAudio, intervalLimits, randomNumber, listTrials, trialCount]); 

  function handleStartStop() {
    setIsPlaying(!isPlaying);
  }

  function handlePlus() {
    setTrialCount(trialCount + 1);
  }
  function handleMinus() {
    if (trialCount > 1) {
      setTrialCount(trialCount - 1);
    };
  }
  function handleDigitChange() {
    setDoubleDigits(!doubleDigits);
  }
  function handleGenerate () {
    const count = doubleDigits ? 2 : 1; 
    const newNumbers = Array.from(
      { length: count },
      () => Math.floor(Math.random() * listAudio.length) + 1
    );
    setTrackedDigits([newNumbers]); 
  }

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const handleReset = () => {
    setTrialCount(1);
    setListTrials([]);
    setShow(false);
  }

  return (
    <>
      <Navbar bg="primary" data-bs-theme="dark">
        <Container>
          <Navbar.Brand href="#home">Digit Monitoring Task</Navbar.Brand>
          <Nav>
            <Nav.Link href="https://uwaterloo.ca/neurocognition-mobility-lab">Neuro-cognition & Mobility Lab</Nav.Link>
          </Nav>
        </Container>
      </Navbar>

      <Card style={{ width: '20vw', boxShadow: "1px 4px 4px #e1e1e1", marginLeft: '40vw' }}>
          <Card.Title>Trial #: {trialCount}</Card.Title>
          <Button variant={isPlaying? "danger" : "success"} size="xxl" onClick={handleStartStop}>
            {isPlaying ? "Stop" : "Start"}
          </Button>
          <div style={{display: 'flex', flexDirection: 'column', width: '15vw', marginLeft: '16px', marginBottom: '8px'}}>
                <Button variant="dark" size="xl" onClick={handlePlus}>+</Button>
                <Button variant="dark" size="xl" onClick={handleMinus}>-</Button>
          </div>
          <Form>
            <Form.Check // prettier-ignore
              type="switch"
              id="track-switch"
              label="Track 2 digits"
              value={doubleDigits}
              checked={doubleDigits}
              onChange={handleDigitChange}
            />
          </Form>
          <div style={{display: 'flex', flexDirection: 'row', width: '15vw', marginLeft: '16px', marginBottom: '8px'}}>
            {trackedDigits.map((number, index) => (
              <Card key={index}>{number}</Card>
            ))}
          </div>
          <Button variant="outline-dark" size="block" onClick={handleShow}>Reset</Button>
      </Card>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Reset trial data?</Modal.Title>
        </Modal.Header>
        <Modal.Body>Warning: This action cannot be undone.</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="danger" onClick={handleReset}>
            Reset data
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default App;
