import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Modal from 'react-bootstrap/Modal';
import 'bootstrap/dist/css/bootstrap.css';
import './styles/styles.css';

import { useState, useEffect } from 'react';

function App() {
  const [trialCount, setTrialCount] = useState(() => {
    return JSON.parse(window.localStorage.getItem('COUNT')) || 1
  });
  const list_audio = [];
  for (let i=1; i<=10; i++) {
    let numSource = import("./audio/" + i + ".mp3")
    let numAudio = new Audio(numSource)
    list_audio.push(numAudio)
  }

  function handlePlay() {
    list_audio[0].play()
  }

  return (
    <>
      <Navbar bg="primary" data-bs-theme="dark">
        <Container>
          <Navbar.Brand href="#home">Digit Monitoring Task</Navbar.Brand>
          <Nav>
            <Nav.Link href="https://uwaterloo.ca/neurocognition-mobility-lab">Neuro-cognition & Mobility Lab</Nav.Link>
          </Nav>
        </Container>
      </Navbar>

      <Card style={{ width: '20vw', boxShadow: "1px 4px 4px #e1e1e1", marginLeft: '40vw' }}>
          <Button variant="outline-dark" size="block" onClick={handlePlay}>Play Sound</Button>
      </Card>
    </>
  );
}

export default App;
